import React, { useContext, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { UserContext } from "../../UserContext";
import io from "socket.io-client";
import Message from "./messages/Message";
let socket;
const Chat = () => {
  let ENDPT = "http://localhost:5000/";
  const [message, setmessage] = useState("");
  const [messages,setmessages]=useState([]);
  const [allmsg,setallmsg]=useState([]);
  const { user, setuser } = useContext(UserContext);
  let { room_id, room_name } = useParams();
  useEffect(() => {
    socket = io(ENDPT, { transports: ["websocket"] });
    console.log(user)
    socket.emit("join", {  room_id, user_id: user._id,name:user.name });
  }, []);

 useEffect(() => {
     socket.on("message",message=>{
       console.log("message",message)
       console.log("messages",allmsg)
         setmessages([...allmsg,message])
     });
     socket.on("output-message",message=>{
       console.log("output message",message)
      setallmsg(message)

     })

 },[])

  const sendMessage = (event) => {
      event.preventDefault();
    console.log(message);
    if (message) {
      socket.emit("sendmessage",message,room_id, () => {
        setmessage("");
      });
    }
  };

  return (
    <div>
      <p>
        {room_id}
        {room_name}
      </p>
      <h1>Chat {JSON.stringify(user)} </h1>
      <Message messages={messages} user_id={user._id} />
      {/* {JSON.stringify(messages,null,"\t")} */}
      <form onSubmit={sendMessage}>
        <input
          placeholder="Eneter message"
          value={message}
          onChange={(e) => setmessage(e.target.value)}
          onKeyPress={(e) => (e.key === "Enter" ? sendMessage : null)}
        />
        <button>Send message</button>
      </form>
    </div>
  );
};

export default Chat;
