import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { UserContext } from "../../UserContext";
import Roomlist from "./Roomlist";
import io from "socket.io-client";
let socket;
const Home = () => {
  let ENDPT = "localhost:5000"; 
  const { user, setUser } = useContext(UserContext);
  const [room, setroom] = useState("");
  useEffect(() => {
    socket = io(ENDPT,{transports:["websocket"]});
    return () => {
      socket.emit("disconnect");
      socket.off();
    };
  }, [ENDPT]);

  const handleSubmit = (e) => {
    e.preventDefault();
    socket.on("create-room", room);
    console.log(room);
    setroom("");
  };

  const rooms = [
    {
      name: "room1",
      _id: "132",
    },
    {
      name: "rome2",
      _id: "323",
    },
  ];
  const setjohn = () => {
    const john = {
      name: "john",
      email: "john@gmail.com",
      password: "123",
      _id: "345",
    };
    setUser(john);
    console.log(user);
  };
  const setTom = () => {
    const tom = {
      name: "tom",
      email: "tom@gmail.com",
      password: "456",
      _id: "468",
    };
    setUser(tom);
  };
  return (
    <div>
      <div className="row">
        <div className="col s12 m6">
          <div className="card blue-grey darken-1">
            <form onSubmit={handleSubmit}>
              <div className="card-content white-text">
                <span className="card-title">
                  Welcome {user ? user.name : ""}
                </span>
              </div>
              <div className="row">
                <div className="input-field col s6">
                  <input
                    value={room}
                    placeholder="Enter Room Name"
                    id="room"
                    type="text"
                    className="validate"
                    onChange={(e) => setroom(e.target.value)}
                  />
                  <label className="active" htmlFor="room">
                    Room Name
                  </label>
                </div>
              </div>
              <button className="waves-effect waves-light btn">Create Room</button>
            </form>
              <div className="card-action">
                <button onClick={setjohn}>Set as john</button>
                <button onClick={setTom}>Set as tom</button>
              </div>
          </div>
        </div>
        <Roomlist rooms={rooms} />
      </div>
      <Link to="/chat">
        <button>Goto chat</button>
      </Link>
    </div>
  );
};

export default Home;
