import React from 'react'

const SingleMessage = ({message,name,text,currentUserId}) => {
    let currentUser=false;
     if(currentUserId === message.user_id){
         currentUser=true;
     }
    // console.log(message)
    return (
        <div>
         {
             currentUser ?
             <div style={{float:"right", color:"red"}} >{name}:{text}</div>:
             <div style={{float:"left", color:"green"}} > {name}:{text}</div>

         }

        </div>
    )
}

export default SingleMessage
